INSERT INTO `demos` VALUES (1,'jquery-File-detail','<html><head></head><body><script src=\"https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js\"></script><br/><input id=\"fUpload\" multiple type=\"file\"/><ul id=\"ulList\"></ul><input id=\"btnShow\" type=\"button\" value=\"Show\"/><br/><script>$(document).ready(function(){$(\"#btnShow\").on(\'click\', function () {    $(\"#ulList\").empty();    var fp = $(\"#fUpload\");    var lg = fp[0]    .files.length; // get length    var items = fp[0].files;    var fragment = \"\";    if (lg > 0) {        for (var i = 0; i < lg; i++) {            var fileName = items[i].name; // get file name            var fileSize = items[i].size; // get file size             var fileType = items[i].type; // get file type          // append li to UL tag to display File info         fragment += \"<li>\" + fileName + \" \" + fileSize + \" bytes. Type :\" + fileType + \"</li>\";        }       $(\"#ulList\").append(fragment);    }});});</script></body> </html>',NULL,NULL,NULL,NULL),(2,'preview-image-jquery','<html>\r\n<head>\r\n<style type=\"text/css\">\r\n.thumb-image{\r\n float:left;width:100px;\r\n position:relative;\r\n padding:5px;\r\n}\r\n</style>\r\n</head>\r\n<body>\r\n<script src=\"https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js\"></script>\r\n<div id=\"wrapper\" style=\"margin-top: 20px;\"><input id=\"fileUpload\" multiple=\"multiple\" type=\"file\"/> \r\n<div id=\"image-holder\"></div>\r\n</div>\r\n\r\n<script>\r\n$(document).ready(function() {\r\n        $(\"#fileUpload\").on(\'change\', function() {\r\n          //Get count of selected files\r\n          var countFiles = $(this)[0].files.length;\r\n          var imgPath = $(this)[0].value;\r\n          var extn = imgPath.substring(imgPath.lastIndexOf(\'.\') + 1).toLowerCase();\r\n          var image_holder = $(\"#image-holder\");\r\n          image_holder.empty();\r\n          if (extn == \"gif\" || extn == \"png\" || extn == \"jpg\" || extn == \"jpeg\") {\r\n            if (typeof(FileReader) != \"undefined\") {\r\n              //loop for each file selected for uploaded.\r\n              for (var i = 0; i < countFiles; i++) \r\n              {\r\n                var reader = new FileReader();\r\n                reader.onload = function(e) {\r\n                  $(\"<img />\", {\r\n                    \"src\": e.target.result,\r\n                    \"class\": \"thumb-image\"\r\n                  }).appendTo(image_holder);\r\n                }\r\n                image_holder.show();\r\n                reader.readAsDataURL($(this)[0].files[i]);\r\n              }\r\n            } else {\r\n              alert(\"This browser does not support FileReader.\");\r\n            }\r\n          } else {\r\n            alert(\"Pls select only images\");\r\n          }\r\n        });\r\n      });\r\n</script>\r\n</body>\r\n</html>',NULL,NULL,NULL,NULL),(3,'jquery-get-dropdown-list','<html>\r\n<head>\r\n<body>\r\n<script src=\"https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js\"></script>\r\nExample 1:\r\n<select id=\"example1\">\r\n    <option value=\"1\">one</option>\r\n    <option value=\"2\">two</option>\r\n    <option value=\"3\">three</option>\r\n    <option value=\"4\">four</option>\r\n</select>Example 2 :\r\n<select id=\"example2\">\r\n    <option id=\"1\">one</option>\r\n    <option id=\"2\">two</option>\r\n    <option id=\"3\">three</option>\r\n    <option id=\"4\">four</option>\r\n</select>\r\n\r\n<script>\r\n$(document).ready(function() {\r\n$(\"#example1\").on(\'change\', function () {\r\n    alert($(this).val());\r\n});\r\n\r\n\r\n$(\"#example2\").on(\'change\', function () {\r\n    //alert($(this).val());\r\n    alert($(this).find(\'option:selected\').attr(\'id\'));\r\n});\r\n});\r\n</script>\r\n\r\n</body>\r\n</html>',NULL,NULL,NULL,NULL),(4,'jquery-generate-table','<html>\r\n<head>\r\n   <script src=\"https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js\"></script>\r\n</head>\r\n