query/1.11.2/jquery.min.js\"></script>\r\n</head>\r\n<body>\r\n\r\n<div id=\"foo\" style=\"color:blue;height:100px;width:200px;background-color:#e2e2e2;\">\r\n	<p>hello world</p>\r\n</div>\r\n<button id=\"btn_1\">Remove All Inline Style</button>\r\n<button id=\"btn_2\">Remove Inline Style width</button>\r\n\r\n<script type=\"text/javascript\">\r\n	$(document).ready(function(){\r\n\r\n		$(\"#btn_1\").on(\'click\', function() {\r\n			$(\"#foo\").removeAttr(\"style\");\r\n		});\r\n		\r\n		$(\"#btn_2\").on(\'click\', function() {\r\n		  $(\"#foo\").css(\"width\", \"\");\r\n		});\r\n	});\r\n</script>\r\n\r\n</body>\r\n</html>',NULL,NULL,NULL,NULL),(29,'get-hidden-field-jquery','<html>\r\n<head>\r\n</head>\r\n<body>\r\n<script src=\"https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js\"></script><br/>\r\n<input id=\"hdName\" value=\"codepedia\" type=\"hidden\"/>\r\n<button id=\"btnShow\"> Show Hidden field Value</button>\r\n<br/>\r\n<script>\r\n$(document).ready(function(){\r\n$(\"#btnShow\").on(\'click\', function () {\r\n  var getValue= $(\"#hdName\").val();\r\n   alert(getValue);   \r\n });\r\n});\r\n</script>\r\n</body>\r\n</html>',NULL,NULL,NULL,NULL),(30,'set-hidden-field-jquery','<html>\r\n<head>\r\n</head>\r\n<body>\r\n<script src=\"https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js\"></script><br/>\r\n<input id=\"txtName\" value=\"\" type=\"text\"/>\r\n<input id=\"hdName\" value=\"codepedia\" type=\"hidden\"/>\r\n<button id=\"btnShow\"> Set Hidden field Value</button>\r\n<br/>\r\n<script>\r\n$(document).ready(function(){\r\n$(\"#btnShow\").on(\'click\', function () {\r\n   var getValue=$(\"#txtName\").val();\r\n   $(\"#hdName\").val(getValue);\r\n   alert(getValue);   \r\n });\r\n});\r\n</script>\r\n</body>\r\n</html>',NULL,NULL,NULL,NULL),(31,'test-demo','<html>\r\n<title>helolo\r\n</title>\r\n<body>\r\n<h2>Hellow world</h2>\r\n</body>\r\n</html>',NULL,NULL,NULL,NULL),(32,'t1','awd',NULL,NULL,'t1',NULL),(33,'t1','<html>\r\n<script>\r\